import React, { Component } from 'react';
import fecha from 'fecha';
import {postService} from "../../services/services";
import {Routes} from "../../services/routes";
import Socket from "./../socket/socket";


class Message extends Component {

    constructor(props) {
        super(props);
        this.state = {
            body: '',
            arrayMessage:[],
        }
    }

    componentWillMount() {
        /*this.setState({
            arrayMessage:this.props.Messages,
        });*/
    }

    // Called only once after the Component is rendered
    /*componentDidMount() {
        let ws = new WebSocket('ws://localhost:4000')
        let socket = new Socket(ws);
        socket.on('message add', this.onMessageAdd.bind(this));
    }



    onMessageAdd(message) {
        console.log(message)
        this.props.Messages.push(message);
    }
*/



    MakeMessage(){
        let token = localStorage.getItem('@websession');

        let headers = {
            Authorization: 'Bearer ' + token,
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        };

        let body = {
            message: this.state.body,
            id_people_message: this.props.Id_people,
        };

        postService(Routes.MAKEMESSAGE, body, headers).then(data => {

            this.setState({
                body:''
            })
        })
    }

    render() {
        return (
            <div className="mesgs">
                <div className="msg_history">
                    {this.props.Messages.map((element, index) => {
                        let createdAt = fecha.format(new Date(element.createdAt), 'DD/MM HH:mm:ss');
                        console.log('message '+element.id_people_message)
                        console.log('people '+ this.props.id_people)

                        if (element.id_people_message === this.props.id_people){
                            return(
                                <div className="incoming_msg" key={index}>
                                    <div className="incoming_msg_img"><img
                                        src="https://ptetutorials.com/images/user-profile.png" alt="sunil"/></div>
                                    <div className="received_msg">
                                        <div className="received_withd_msg">
                                            <span className="time_date">{element.author}</span>
                                            <p>{element.message}</p>
                                            <span className="time_date">{createdAt}</span></div>
                                    </div>
                                </div>
                            )
                        }else{
                            return(
                            <div  className="outgoing_msg" key={index}>
                                <div className="sent_msg">
                                    <span className="time_date">{element.author}</span>
                                    <p>{element.message}</p>
                                    <span className="time_date">{createdAt}</span></div>
                            </div>
                            )
                        }
                    })}
                </div>
                <div className="type_msg">
                    <div className="input_msg_write">
                        <input type="text" className="write_msg" placeholder="Escribe Un Mensaje"
                               value={this.state.body}
                               onChange={(e) => this.setState({
                                   body: e.target.value
                               })}
                        />
                        <button onClick={()=>this.MakeMessage()} className="msg_send_btn" type="button"><i className="fa fa-paper-plane-o"
                                                                          aria-hidden="true"/></button>
                    </div>
                </div>
            </div>
        )
    }
}

export default Message;
