import React, { Component } from 'react';
import Message from './../message/index'
import User from './../user/index'
import {getService} from "./../../services/services";
import {Routes} from "./../../services/routes";
import {postService} from "../../services/services";

class ContainerChannels extends Component {

    constructor(props) {
        super(props);
        this.state = {
            arraypeople: [],
            arrayMessage: [],
            Id_people:''
        }
    }


    componentWillMount() {
        this.makeListPeople()
    }



    makeListPeople(){
        let token = localStorage.getItem('@websession');

        let headers = {
            Authorization: 'Bearer ' + token,
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        };

        getService(Routes.LISTPEOPLE, headers).then(data => {
            this.setState({
                arraypeople: data,
            });
        });
    }

    findMessagePeople(e){
        this.setState({
            Id_people:e,
        });
        let token = localStorage.getItem('@websession');

        let headers = {
             Authorization: 'Bearer ' + token,
            'Content-Type': 'application/json',
            'Accept': 'application/json'
        };

        let body = {
            Id_people: e,
        };

        postService(Routes.LISTMESSAGE, body, headers).then(data => {
            console.log(data)
            this.setState({
                arrayMessage: data,
            });
        })
    }

    render() {
        return (
                <div className="inbox_msg">
                    <div className="inbox_people">
                        <User/>
                        <div className="inbox_chat">
                            {this.state.arraypeople.map((element, index) => {
                                return(
                                    <div className="chat_list active_chat" key={index} onClick={()=>this.findMessagePeople(element.id)}>
                                        <div className="chat_people">
                                            <div className="chat_img"><img
                                                src="https://ptetutorials.com/images/user-profile.png" alt="sunil"/></div>
                                            <div className="chat_ib">
                                                <h5>{element.name} {element.last_name}</h5>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })}
                        </div>
                    </div>
                    <Message Messages={this.state.arrayMessage} Id_people={this.state.Id_people}/>
                </div>
        )
    }
}

export default ContainerChannels;
